mapper.select:
SELECT

from tb_account

    del_flag =0 AND type = #{filter.type}

        AND username = #{filter.username}



        AND phone like #{phone}



        AND nickname LIKE #{nickname}


ORDER BY id DESC

mapper.update:
UPDATE
  tb_account
SET
  password = #{password}
WHERE
  id=#{id}

UPDATE
  tb_account
SET
phone = #{phone}
WHERE
   id=#{id}

mapper.select:
SELECT * from tb_account
del_flag =0 AND openid = #{openId}
ORDER BY id DESC

SELECT
FROM tb_account a WHERE a.del_flag=0 and a.phone in
#{phone}
